//OPS MATEMÁTICAS

function avistacomdesconto(valorProduto) { //Boleto a vista
    var desconto = valorProduto.value * 0.10;
    var resultado = valorProduto.value - desconto;
    alert('Valor a pagar: R$' + parseFloat(resultado).toFixed(2));
}

function avista(valorProduto) { //Cartão a vista
    var resultado = valorProduto.value;
    alert('Valor a pagar: R$' + parseFloat(resultado).toFixed(2));
}

function aprazo(valorProduto) { //Cartão parcelado
    document.getElementById("inputParcelas").innerHTML = "<b style='margin-left:2px'> PARCELAS: </b> <input type='number' class='form- control' id='parcelas'/>"
        + "<input type='button' class='btn btn-default' onclick='aprazoparcelado(valorProduto,parcelas)' style='background-color:#000000; color: #ffffff; margin:5px;' value='Comprar'><br /><br/>";
}

function aprazoparcelado(valorProduto, parcelas) { //Parcelas

    if (parcelas.value <= 12 && parcelas.value > 1) {

        var total = parseFloat(valorProduto.value);
        var valorParcela = 0;
        var resultado = 0;

        for (let i = parcelas.value; i > 0; i--) {

            valorParcela += total * 0.02;
        }


        resultado = total + valorParcela;

        var valorParcelado = (resultado / parcelas.value);;

        alert('Valor a pagar: R$' + parseFloat(resultado).toFixed(2) + '\n' +
            'Valor a pagar por parcela: R$' + parseFloat(valorParcelado).toFixed(2));
    }

    else {
        alert('Parcelamos no maximo em 12 vezes') //Limite
    }

}
